import React from 'react';
import { useState, useEffect} from 'react';
import PropTypes, { string } from 'prop-types';

import { 
	Panel, 
	PanelHeader, 
	PanelHeaderBack, 
	Header, 
	Button, 
	Group, 
	Cell, 
	Div, 
	Avatar, 
	Tabs, 
	TabsItem, 
	List, 
	Footer, 
	PopoutWrapper,
	Textarea,
	Checkbox,
	Link,
	Radio,
	FormLayout, 
	FormItem, 
	Input, 
} from '@vkontakte/vkui';
import {Icon28SettingsOutline, Icon28LogoVk} from '@vkontakte/icons';
import Web3 from 'web3';


const containerStyles = {
	display: 'flex',
	flexDirection: 'column',
	justifyContent: 'center',
	width: '100%',
	};

const Home = ({ id, go}) => {

	const [fetching, setFetching] = React.useState(false);

	// Сосотояния пользователей
	const [userInWhiteList, setUserInWhiteList] = useState(true)


	// Состояния страниц

	const [homePage, setHomePage] = useState(true)
	const [buyPage, setBuyPage] = useState(false)
	const [createPage, setCreatePage] = useState(false)
	const [settingsPage, setSettingsPage] = useState(false)
	const [inventoryPage, setInventoryPage] = useState(false)

	const [showGroup, setShowGroup] = useState(false)
	

	// const initUsers = getRandomUsers(10);
	// console.log(initUsers)

	// Состояние мероприятия
	const [nameEvent, setNameEvent] = useState('')
	const [formatEvent, setFormatEvent] = useState('')
	const [descriptionEvent, setDescriptionEvent] = useState('')
	const [quantityEvent, setQuantityEvent] = useState('')

	const allEvents = 6
	let fragmentEvent = null

	// Функции сосотояния страниц
	const setHomeBuy = () => {
		setHomePage(!homePage);
		setBuyPage(!buyPage);
	}
	const setHomeCreate = () => {
		setHomePage(!homePage);
		setCreatePage(!createPage);
	}
	const setHomeSettings = () => {
		setHomePage(!homePage);
		setSettingsPage(!settingsPage);
	}
	const setHomeInventory = () => {
		setHomePage(!homePage);
		setInventoryPage(!inventoryPage);
	}
	const setSettingsInventory = () => {
		setSettingsPage(!settingsPage);
		setInventoryPage(!inventoryPage);
	}

	// Для коннектов
	const [isConnected, setIsConnected] = useState(false);
	const [walletIsConnected, setWalletIsConnected] = useState(false);
  const [ethBalance, setEthBalance] = useState("");
  const [ethAcc, setEthAcc] = useState('');
  const [ethAdd, setEthAdd] = useState('');
  // const [popout, setPopout] = useState(null);
  // const clearPopout = () => setPopout(null);



	// Для интерфейса

	const [name, setName] = React.useState('');
	// const [email, setEmail] = React.useState('');
  // const [purpose, setPurpose] = React.useState('');
  // const [showPatronymic, setShowPatronymic] = React.useState(true);
	const [symbol, setSymbol] = React.useState('');
	const [quantity, setQuantity] = React.useState('');
	const [description, setDescription] = React.useState('');
	const [format, setFormat] = React.useState(false);

	const [isCreated, setIsCreated] = useState(false);


	// Создание блока с разметкой для сущ мероприятий

	const [plusBlock, setPlusBlock] = useState([])
	

  const onChange = (e) => {
    const { name, value } = e.currentTarget;

    const setStateAction = {
			name: setName,
      email: setEmail,
      purpose: setPurpose,
			quantity: setQuantity,
			symbol: setSymbol,
			description: setDescription,
    }[name];

    setStateAction && setStateAction(value);
  };

	// Коннекты кошелька и контракта

	const detectCurrentProvider = () => {
    let provider;
    if (window.ethereum) {
      provider = window.ethereum;
    } else if (window.web3) {
      provider = window.web3.currentProvider;
    } else {
      console.log("Non-ethereum browser detected. You should install Metamask");
    }
    return provider;
  };
  
  const onConnect = async() => {
    try {
      const currentProvider = detectCurrentProvider();
			const ABI = [
				{
					"inputs": [
						{
							"internalType": "string",
							"name": "_name",
							"type": "string"
						},
						{
							"internalType": "string",
							"name": "_symbol",
							"type": "string"
						},
						{
							"internalType": "string",
							"name": "_discription",
							"type": "string"
						},
						{
							"internalType": "uint256",
							"name": "_supply",
							"type": "uint256"
						},
						{
							"internalType": "bool",
							"name": "_format",
							"type": "bool"
						}
					],
					"name": "createEvent",
					"outputs": [
						{
							"internalType": "address",
							"name": "",
							"type": "address"
						}
					],
					"stateMutability": "nonpayable",
					"type": "function"
				},
				{
					"inputs": [],
					"stateMutability": "nonpayable",
					"type": "constructor"
				},
				{
					"inputs": [],
					"name": "addressStor",
					"outputs": [
						{
							"internalType": "address",
							"name": "",
							"type": "address"
						}
					],
					"stateMutability": "view",
					"type": "function"
				},
				{
					"inputs": [
						{
							"internalType": "uint256",
							"name": "_id",
							"type": "uint256"
						}
					],
					"name": "addressTiket",
					"outputs": [
						{
							"internalType": "address",
							"name": "",
							"type": "address"
						}
					],
					"stateMutability": "view",
					"type": "function"
				},
				{
					"inputs": [
						{
							"internalType": "uint256",
							"name": "",
							"type": "uint256"
						}
					],
					"name": "incidents",
					"outputs": [
						{
							"internalType": "uint256",
							"name": "id",
							"type": "uint256"
						},
						{
							"internalType": "address",
							"name": "from",
							"type": "address"
						},
						{
							"internalType": "address",
							"name": "fabric",
							"type": "address"
						},
						{
							"internalType": "uint256",
							"name": "supply",
							"type": "uint256"
						},
						{
							"internalType": "string",
							"name": "name",
							"type": "string"
						},
						{
							"internalType": "string",
							"name": "symbol",
							"type": "string"
						},
						{
							"internalType": "string",
							"name": "discription",
							"type": "string"
						},
						{
							"internalType": "bool",
							"name": "format",
							"type": "bool"
						},
						{
