import React, { useState, useEffect } from 'react';
import bridge from '@vkontakte/vk-bridge';
import { View, ScreenSpinner, AdaptivityProvider, AppRoot, ConfigProvider, SplitLayout, SplitCol } from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';

import Home from './panels/Home';
// import Connect from './panels/Connect';
import Buy from './panels/Buy'
import Settings from './panels/Settings'
import Inventory from './panels/Inventory'
import Create from './panels/Create'
import Privacy from './panels/privacy'

const App = () => {
	const [scheme, setScheme] = useState('bright_light')
	const [activePanel, setActivePanel] = useState('home');
	const [fetchedUser, setUser] = useState(null);
	const [popout, setPopout] = useState(null);

	bridge.send('VKWebAppAddToFavorites')
	.then((data) => { 
		if (data.result) {
		// Мини-приложение или игра добавлены в избранное
		}
	})
	.catch((error) => {
		// Ошибка
		console.log(error);
	});

	useEffect(() => {
		bridge.subscribe(({ detail: { type, data }}) => {
			if (type === 'VKWebAppUpdateConfig') {
				setScheme(data.scheme)
			}
		});

		async function fetchData() {
			const user = await bridge.send('VKWebAppGetUserInfo');
			setUser(user);
			setPopout(null);
		}
		fetchData();
	}, []);

	const go = e => {
		setActivePanel(e.currentTarget.dataset.to);
	};

	return (
		<ConfigProvider scheme={scheme}>
			<AdaptivityProvider>
				<AppRoot>
					<SplitLayout popout={popout}>
						<SplitCol>
							<View activePanel={activePanel}>
								<Home id='home' go={go}/>
								{/* <Connect id='connect' go={go}/> */}
								<Buy id='buy' go={go}/>
								<Settings id='settings' go={go}/>
								<Inventory id='inventory' go={go}/>
								<Create id='create' go={go}/>
								<Buy id='buy' go={go}/>
								<Privacy id='privacy' go={go}/>
							</View>
						</SplitCol>
					</SplitLayout>
				</AppRoot>
			</AdaptivityProvider>
		</ConfigProvider>
	);
}

export default App;
