import React from 'react';
import PropTypes from 'prop-types';
import { useState, useEffect} from 'react';
import { 
	Panel, 
	PanelHeader, 
	PanelHeaderBack, 
	Header, 
	Button, 
	Group, 
	Cell, 
	Div, 
	Avatar, 
	Tabs, 
	TabsItem, 
	FormLayout, 
	FormItem, 
	Input, 
	FormLayoutGroup, 
	CellButton, 
	Select, 
	SegmentedControl, 
	Textarea,
	Checkbox,
	Link
} from '@vkontakte/vkui';
import {Icon28SettingsOutline, Icon28LogoVk} from '@vkontakte/icons';

const addressItems = [
  { label: 'Почтовый индекс', name: 'zip' },
  { label: 'Страна', name: 'country' },
  { label: 'Город', name: 'city' },
];

const Create = ({ id, go}) => {

	const [email, setEmail] = React.useState('');
  const [purpose, setPurpose] = React.useState('');
  const [showPatronymic, setShowPatronymic] = React.useState(true);

  const onChange = (e) => {
    const { name, value } = e.currentTarget;

    const setStateAction = {
      email: setEmail,
      purpose: setPurpose,
    }[name];

    setStateAction && setStateAction(value);
  };

  const onShowPatronymic = () => setShowPatronymic(true);

  const onRemove = () => setShowPatronymic(false);

	return (
		<Panel id={id}>
			<PanelHeader left={<PanelHeaderBack onClick={go} data-to="home"/>}> 
				Создание мероприятия
			</PanelHeader>
			<Group>
				<FormLayout>


					{/* Email */}
					<FormItem
					top="E-mail"
					status={email ? 'valid' : 'error'}
					bottom={
						email ? '' : 'Обязательное поле!'
					}
					>
					<Input type="email" name="email" value={email} onChange={onChange} />
					</FormItem>


					{/* ФИО */}
					<FormLayoutGroup mode="horizontal">
					<FormItem top="Имя">
						<Input />
					</FormItem>
					<FormItem top="Фамилия">
						<Input />
					</FormItem>
					</FormLayoutGroup>

					{/* Дополнительное поле: Отчество */}
					{!showPatronymic ? (
					<CellButton onClick={onShowPatronymic}>Указать отчество</CellButton>
					) : (
					<FormItem
						removable
						onRemove={onRemove}
						top="Отчество"
						bottom="Если у вас нет отчества — удалите этот пункт."
					>
						<Input />
					</FormItem>
					)}


					{/* Пол */}
					<FormItem top="Пол">
					<Select
						placeholder="Выберите пол"
						options={[
						{
							value: '0',
							label: 'Мужской',
						},
						{
							value: '1',
							label: 'Женский',
						},
						]}
					/>
					</FormItem>


					{/* Документ */}
					<FormItem top="Тип документа">
					<SegmentedControl
						size="m"
						name="type"
						options={[
						{
							label: 'Паспорт РФ',
							value: 'russian',
						},
						{
							label: 'Заграничный',
							value: 'international',
						},
						]}
					/>
					</FormItem>

					{addressItems.map(({ label, name }) => (
					<FormItem top={label} key={name}>
						<Input name={name} />
					</FormItem>
					))}
					<FormItem
					top="Тип мероприятия"
					bottom={purpose ? '' : 'Пожалуйста, укажите тип вашего мероприятия'}
					status={purpose ? 'valid' : 'error'}
					>
					<Select
						placeholder="Выберите тип мероприятия"
						onChange={onChange}
						value={purpose}
						name="purpose"
						options={[
						{
							value: '0',
							label: 'Бизнес и финансы',
						},
						{
							value: '1',
							label: 'Обучение',
						},
						{
							value: '2',
							label: 'Развлечения',
						},
						{
							value: '3',
							label: 'Спорт',
						},
						]}
					/>
					</FormItem>
					<FormItem top="Коротко о мероприятии">
					<Textarea />
					</FormItem>
					<Checkbox>
					Согласен на <Link>обраб</Link>
					</Checkbox>
					<FormItem>
					<Button size="l" stretched>
						Зарегистрироваться
					</Button>
					</FormItem>
				</FormLayout>
			</Group>
		</Panel>
	);
};

Create.propTypes = {
	id: PropTypes.string.isRequired,
	go: PropTypes.func.isRequired,
	fetchedUser: PropTypes.shape({
		photo_200: PropTypes.string,
		first_name: PropTypes.string,
		last_name: PropTypes.string,
		city: PropTypes.shape({
			title: PropTypes.string,
		}),
	}),
};

export default Create;
