import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
// import {ethers} from 'ethers'
import { Button, Panel, PanelHeader, PanelHeaderBack,  } from '@vkontakte/vkui';

import persik from '../img/persik.png';
import './Persik.css';

const Inventory = () => {
	const [userAccount, setUserAccount] = userAccount(null);

	const onConnect = () => {
		if (window.ethereum) {
			//если есть метамаск
			window.ethereum.request({method: "eth_requestAccounts"}).then
		} else {
			alert("Установите МетаМаск!")
		}
	}

	return(
		<Panel>
			<PanelHeader>
				Inventory
			</PanelHeader>
			{userAccount ? (
				<span>1111</span>
				) : (
					<><h2>Подключите свой кошелек к приложению!</h2>
					<Button>Подключить кошелек MetaMask</Button></>
					)}
			{/* <><h2>Подключите свой кошелек к приложению!</h2>
			<Button>Подключить кошелек MetaMask</Button></> */}
		</Panel>
	)
}
// Inventory.propTypes = {
// 	id: PropTypes.string.isRequired,
// 	go: PropTypes.func.isRequired,
// };

export default Inventory;
