import React, { useState, useEffect } from 'react';
import bridge from '@vkontakte/vk-bridge';
import { View, ScreenSpinner, AdaptivityProvider, AppRoot, ConfigProvider, SplitLayout, SplitCol } from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';

import Home from './panels/Home';
import Persik from './panels/Persik';

const App = () => {
	const platform = usePlatform();
  const isVKCOM = platform === Platform.VKCOM;
  const { viewWidth } = useAdaptivityConditionalRender();

  return (
    <ConfigProvider>
      <AdaptivityProvider>
        <AppRoot>
          <SplitLayout header={!isVKCOM && <PanelHeader separator={false} />}>
            {viewWidth.tabletPlus && (
              <SplitCol width={280} className={viewWidth.tabletPlus.className}>
                <Header id="nav">Navigation</Header>
              </SplitCol>
            )}
            <SplitCol autoSpaced>
              <View activePanel="profile">
                <Header id="profile">Profile</Header>
              </View>
            </SplitCol>
          </SplitLayout>
        </AppRoot>
      </AdaptivityProvider>
    </ConfigProvider>
  );
}

const MainScreens = () => {
  return (
    <View activePanel="profile">
      <Panel id="profile">Profile</Panel>
    </View>
  );
};

const SideCol = () => {
  return <Panel id="nav">Navigation</Panel>;
};

ReactDOM.render(<App />, document.getElementById('root'));
export default App;
